import "./App.css";
import { useState } from "react";
import Axios from "axios";

function App() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [ageRange, setAgeRange] = useState("");
  const [city, setCity] = useState("");
  const [role, setRole] = useState("");
  const [salary, setSalary] = useState(0);

  const addEmployee = (e) => {
    e.preventDefault();
    Axios.post("http://localhost:3000/create", {
      firstName: firstName,
      lastName: lastName,
      ageRange: ageRange,
      city: city,
      role: role,
      salary: salary,
    }).then(() => {
      console.log("employee added");
    });
  };

  return (
    <div className="App">
      <div className="information">
        <form>
          <div className="form-group">
            <label htmlFor="firstNameInput">First Name</label>
            <input
              type="text"
              className="form-control"
              id="firstNameInput"
              placeholder="First Name"
              onChange={(e) => {
                setFirstName(e.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label htmlFor="firstNameInput">Last Name</label>
            <input
              type="text"
              className="form-control"
              id="lastNameInput"
              placeholder="Last Name"
              onChange={(e) => {
                setLastName(e.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label htmlFor="ageRangeSelect">Age</label>
            <select
              className="form-control"
              id="ageRangeSelect"
              defaultValue="Age"
              onChange={(e) => {
                setAgeRange(e.target.value);
              }}
            >
              <option>18-24</option>
              <option>25-34</option>
              <option>35-44</option>
              <option>45-54</option>
              <option>55-64</option>
              <option>65+</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="cityInput">City</label>
            <input
              type="text"
              className="form-control"
              id="cityInput"
              placeholder="City"
              onChange={(e) => {
                setCity(e.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label htmlFor="roleInput">Role</label>
            <input
              type="text"
              className="form-control"
              id="roleInput"
              placeholder="Role"
            />
          </div>
          <div className="form-group" id="salary-form-group">
            <label htmlFor="salaryInput">Salary</label>

            <span className="salary-input-container">
              <div className="input-group-prepend">
                <span className="input-group-text" id="currency-prepend">
                  £
                </span>
              </div>
              <input
                type="number"
                className="form-control"
                id="salaryInput"
                placeholder="Salary"
                onChange={(e) => {
                  setSalary(e.target.value);
                }}
              />
            </span>
          </div>
          <button className="btn btn-dark" onClick={addEmployee}>
            Add Employee
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
